#!/bin/bash

# === Renet Compose Support ===
_use_renet() {
  [[ -n "$REPOSITORY_NETWORK_ID" ]] && command -v renet &>/dev/null
}
_compose() {
  if _use_renet; then
    renet compose --network-id "$REPOSITORY_NETWORK_ID" -- "$@"
  else
    docker compose "$@"
  fi
}

prep() {
  docker pull mysql:8.0 && \
  docker pull wordpress && \
  mkdir -p wordpress db
  return $?
}

up() {
  _compose up -d
  return $?
}

down() {
  _compose down -v
  return $?
}
